// components/ProjectsSection.js
import React from 'react';
import './ProjectsSection.css';

const ProjectsSection = () => {
  const projects = [
    {
      id: 1,
      title: 'Résidence Émeraude',
      location: 'Paris 16ème',
      description: 'Un ensemble résidentiel d\'exception offrant des vues imprenables sur la Tour Eiffel. Conçu par des architectes renommés, ce lieu incarne l\'élégance parisienne.',
      image: '/images/villa2/photo5.jpg',
      status: 'Livraison 2025'
    },
    {
      id: 2,
      title: 'Villa Azur',
      location: 'Saint-Tropez',
      description: 'Une propriété d\'exception face à la mer avec accès privatif à la plage. Ce joyau méditerranéen allie luxe contemporain et charme traditionnel de la Côte d\'Azur.',
      image: '/images/cytes/photo1.jpg',
      status: 'Disponible'
    },
  ];

  return (
    <section id="projets" className="projects-section fade-in-up" style={{ animationDelay: '0.6s' }}>
      <div className="section-header">
        <h2>Nos Projets d'Exception</h2>
        <div className="separator"></div>
        <p>Des résidences exclusives sélectionnées pour une clientèle exigeante</p>
      </div>

      <div className="featured-projects">
        {projects.map((project, index) => (
          <div 
            key={project.id} 
            className={`featured-project fade-in-up ${index === 0 ? 'first-project' : 'second-project'}`} 
            style={{ animationDelay: `${0.8 + index * 0.2}s` }}
          >
            <div className="project-image-container">
              <img src={project.image} alt={project.title} className="project-image" />
              <div className="project-status">{project.status}</div>
            </div>
            <div className="project-info">
              <h3>{project.title}</h3>
              <p className="project-location">{project.location}</p>
              <div className="project-separator"></div>
              <p className="project-description">{project.description}</p>
              <button className="project-details-btn">Découvrir</button>
            </div>
          </div>
        ))}
      </div>

      <div className="projects-cta fade-in-up" style={{ animationDelay: '1.2s' }}>
        <p>Nous avons d'autres projets exclusifs en préparation</p>
        <a href="#contact" className="contact-cta-btn">Consultez-nous</a>
      </div>
    </section>
  );
};

export default ProjectsSection;
